// advance shader program

uniform sampler2D samplerBlur3;
uniform sampler2D samplerBlur2;
uniform sampler2D samplerBlur;
uniform sampler2D samplerPrev;
uniform sampler2D samplerNoise;

uniform vec3 rnd;
uniform vec2 pixelSize;
uniform vec2 mouse;
uniform float time;

vec2 gradient(sampler2D sampler, vec2 uv, vec2 d, vec4 selector){
	vec4 dX = texture2D(sampler, uv + vec2(1., 0.) * d)
	        - texture2D(sampler, uv - vec2(1., 0.) * d);
	vec4 dY = texture2D(sampler, uv + vec2(0., 1.) * d)
	        - texture2D(sampler, uv - vec2(0., 1.) * d);
	return vec2(dot(dX, selector), dot(dY, selector));
}

void main()
{
    vec2 uv = gl_FragCoord.xy * pixelSize;
    vec2 zoomUv = mouse + (uv - mouse)*0.99;

    vec4 rawPrev = texture2D(samplerPrev, uv);
    vec4 rawBlur = texture2D(samplerBlur, uv);
    vec4 noise = texture2D(samplerNoise, uv + (rnd.yz-0.5)*2.);

    vec2 uvR = uv + gradient(samplerBlur, uv, pixelSize * 4., vec4(8,0,0,0)) * pixelSize;
    float R = texture2D(samplerPrev, uvR).r * 0.975;
    R += (rawPrev - rawBlur).r * 24. / 256.;
    R += (noise.r - 0.5) * 12. / 256.;

    vec2 uvG = uv + gradient(samplerBlur3, uv, pixelSize * 8., vec4(16,-1,0,0)) * pixelSize;
    float G = texture2D(samplerPrev, uvG).g;
    G += (noise.g - 0.5) * 64. / 256.;

    vec4 RGBA = vec4(R, G, 0, 1.);
    float pointer = max(0., 1. - length((uv-mouse)/pixelSize/8.));
    RGBA = mix(RGBA, vec4(0.93, 0.04, 0.23,1.), pointer);

    RGBA = clamp(RGBA,0.,1.);
    outColor = RGBA.rgra;
}

