// horizontal blur fragment shader

uniform sampler2D sampler;
uniform vec2 pixelSize;

IN vec2 texCoord;
{{MODULES_HEAD}}
void main()
{
    vec2 uv = gl_FragCoord.xy * pixelSize;

	vec4 sum = vec4(0.0);
	// Gaussian factors from https://github.com/mattdesl/lwjgl-basics/wiki/ShaderLesson5
	sum += texture2D(sampler, vec2(-4,0) * pixelSize + uv ) * 0.0162162162;
	sum += texture2D(sampler, vec2(-3,0) * pixelSize + uv ) * 0.0540540541;
	sum += texture2D(sampler, vec2(-2,0) * pixelSize + uv ) * 0.1216216216;
	sum += texture2D(sampler, vec2(-1,0) * pixelSize + uv ) * 0.1945945946;
	sum += texture2D(sampler, vec2( 0,0) * pixelSize + uv ) * 0.2270270270;
	sum += texture2D(sampler, vec2( 1,0) * pixelSize + uv ) * 0.1945945946;
	sum += texture2D(sampler, vec2( 2,0) * pixelSize + uv ) * 0.1216216216;
	sum += texture2D(sampler, vec2( 3,0) * pixelSize + uv ) * 0.0540540541;
	sum += texture2D(sampler, vec2( 4,0) * pixelSize + uv ) * 0.0162162162;

    {{MODULE_COLOR}}
    outColor = sum;
    outColor.a = 1.;
}

